import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface ICursurControl {
  cursur: number;
}

const initialState = {
  cursur: -2,
};

const cursurControl = createSlice({
  name: 'cursurControl',
  initialState,
  reducers: {
    setCursur: (state: ICursurControl, action: PayloadAction<number>) => {
      state.cursur = action.payload;
    },
  },
});

export const { setCursur } = cursurControl.actions;

export default cursurControl.reducer;
