import { SubscriptionImage, SubscriptionImageSm } from 'assets';
import Footer from 'components/Footer';
import Header from 'components/Header';
import SearchInput from 'components/SearchInput';
import { useCallback, useEffect, useState } from 'react';
import { useQuery } from 'react-query';
import { fetcher } from 'utils';

import styles from './main.module.scss';

function Main() {
  const [isShow, setIsShow] = useState(false);
  const [cursor, setCursor] = useState(-1);
  const [value, setValue] = useState('');

  const { data } = useQuery(['data'], () => fetcher('암'));

  const keyboardNavigation = useCallback(
    (e: KeyboardEvent) => {
      if (data === undefined) return;

      if (e.key === 'ArrowDown' && isShow) {
        setCursor((prev) => (prev < data.length - 1 ? prev + 1 : prev));
      }
      if (e.key === 'ArrowUp' && isShow) {
        setCursor((prev) => (prev > 0 ? prev - 1 : 0));
      }
      if (e.key === 'Escape') {
        setCursor(-1);
        setIsShow(false);
      }
    },
    [data, isShow, setCursor, setIsShow]
  );

  useEffect(() => {
    window.addEventListener('keydown', keyboardNavigation);
  }, [keyboardNavigation]);

  const mouseDown = (idx: number) => {
    setCursor(idx);
  };
  return (
    <>
      <Header />
      <main className={styles.main}>
        <section className={styles.search}>
          <div className={styles.title}>
            국내 모든 임상시험 검색하고
            <br />
            온라인으로 참여하기
          </div>
          <SearchInput />
        </section>
        <section className={styles.subscription}>
          <div className={styles.content}>
            <div className={styles.description}>
              <div className={styles.desc}>
                새로운 임상시험이 등록되면
                <span className={styles.wordbreak}> 문자로 알려드려요.</span>
              </div>
              <button className={styles.button} type="button">
                임상시험 소식 받기
              </button>
            </div>
            <SubscriptionImage className={styles.image} />
            <SubscriptionImageSm className={styles['image-sm']} />
          </div>
        </section>
      </main>
      <Footer />
    </>
  );
}

export default Main;
function useQueryDebounce(value: string, arg1: number) {
  throw new Error('Function not implemented.');
}
