import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface ICursorControl {
  cursor: number;
}

const initialState = {
  cursor: -2,
};

const cursorControl = createSlice({
  name: 'cursorControl',
  initialState,
  reducers: {
    setCursor: (state: ICursorControl, action: PayloadAction<number>) => {
      state.cursor = action.payload;
    },
  },
});

export const { setCursor } = cursorControl.actions;

export default cursorControl.reducer;
