import { SetStateAction, useCallback, Dispatch } from 'react';

import { LeftArrowIcon, MagnifierIcon } from 'assets';

import SearchList from 'components/SearchList';
import { IItem } from 'types/search.d';

import styles from './search-input.module.scss';

interface IProps {
  searchValue: string;
  setSearchValue: Dispatch<SetStateAction<string>>;
  isSearching: boolean;
  setIsSearching: Dispatch<SetStateAction<boolean>>;
  data: void | { sickCd: string; sickNm: string }[] | undefined;
  cursor: number;
  mouseDown: (idx: number) => void;
}

function MobileSearchInput({
  searchValue,
  setSearchValue,
  isSearching,
  setIsSearching,
  data,
  cursor,
  mouseDown,
}: IProps) {
  const onChange = useCallback(
    (e: { target: { value: SetStateAction<string> } }) =>
      setSearchValue(e.target.value),
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [searchValue]
  );

  const toggleSearchInput = () => {
    setIsSearching((prev) => !prev);
    // setSearchValue('');
  };

  return (
    <>
      <div
        role="button"
        tabIndex={-1}
        className={styles['mobile-search-input']}
        onClick={toggleSearchInput}
      >
        {/* input에 입력된 값이 들어가야함 */}
        <span>질환명을 입력해 주세요.</span>
        <MagnifierIcon />
      </div>
      <div
        data-id="mobile"
        className={styles['mobile-container']}
        style={isSearching ? { display: 'flex' } : { display: 'none' }}
      >
        <div className={styles['input-container']}>
          <LeftArrowIcon onClick={toggleSearchInput} />
          <input
            type="search"
            className={styles.input}
            placeholder="질환명을 입력해 주세요."
            onChange={onChange}
          />
          <MagnifierIcon />
        </div>
        {data ? (
          <SearchList data={data} cursor={cursor} mouseDown={mouseDown} />
        ) : (
          <p>검색어 없음</p>
        )}
      </div>
    </>
  );
}

export default MobileSearchInput;
